package chapter2;

public class IfElse {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("코드 시작");
		
		if(true) {
			System.out.println("이 if문은");
			System.out.println("참이므로");
			System.out.println("코드가 실행됩니다.");
		}
			System.out.println("코드 끝");
	
	
			
	if (false){
		System.out.println("이 if문은");
		System.out.println("참이므로");
		System.out.println("코드가 실행됩니다.");
	}
		System.out.println("코드 끝");
	}
}
